mapper.insert:

	SELECT LAST_INSERT_ID()

INSERT INTO inventory(
	film_id,
	store_id
) VALUES (
	#{filmId},
	#{storeId}
)

mapper.select:
SELECT i.store_id storeId, f.title title, COUNT(*) AS totalInventory, SUM(NOT inventory_in_stock(i.inventory_id)) AS rental, SUM(inventory_in_stock(i.inventory_id)) AS stock FROM film f, inventory i WHERE f.film_id = i.film_id
AND i.store_id = #{storeId}
AND title LIKE CONCAT('%',#{searchWord},'%')
GROUP BY i.film_id, i.store_id LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(DISTINCT(i.film_id)) AS totalInventory FROM film f, inventory i WHERE f.film_id = i.film_id
AND i.store_id = #{storeId}
AND title LIKE CONCAT('%',#{searchWord},'%')
